import React, {Component} from 'react';

import spiral from './spiral.svg';
import dashed from './dashed.svg';
import circle from './circle.svg';

import './Game.css';
import './GameQueries.css';


class Game extends Component {
  state = {
    turns : 0,
    totalTurns: 0,
    degree: 0,
    difference: 1000,
    last: new Date().getTime(),
    challenges: ["Parson's Green", "Brave New York", "Sex Toys", "Sheer Bravery"],
    showChallenge: false
  }

  play = (e) => {
    e.preventDefault();
    this.getRandomNum();

    requestAnimationFrame(this.renderChallenge);
  }

  getRandomNum = () => {
    let randomNum = Math.floor((Math.random() * 20) + 10);

    this.setState({
      turns : randomNum,
      totalTurns: randomNum
    },
     () => { this.renderChallenge() }
    );
  }

  renderChallenge = () => {
    const { challenges, turns } = this.state;
    let position = (this.state.totalTurns * 90) / 360 % 1;
    let now = new Date().getTime();

    if(turns === 0) {

      if(position === 0) {
        return <span className='challenge'>{challenges[0]}</span>
      }
      if(position === 0.25) {
        return <span className='challenge'>{challenges[1]}</span>

      }
      if(position === 0.5) {
        return <span className='challenge'>{challenges[2]}</span>

      }
      if(position === 0.75) {
        return <span className='challenge'>{challenges[3]}</span>
      }

      this.setState({
        totalTurns: 0,
        showChallenge: true,
        difference: 1000
      });
      return;
    }

    if(!this.state.last || now - this.state.last > this.state.difference) {

      this.setState((prevState) => ({
        last: now,
        showChallenge: false,
        turns: prevState.turns - 1,
        degree: prevState.degree + 90,
        difference: (prevState.turns >= prevState.turns / 5) ? prevState.difference * 0.85 : prevState.difference * 1.25
      }))
    }
    requestAnimationFrame(this.renderChallenge);
  }

  render() {
    // console.log("TOTAL turns",this.state.totalTurns)
    // console.log("turns * 90 / 360: ",(((this.state.totalTurns * 90) / 360) % 1))
      // (turns * 90) / 360
      // split at decimal point
      // work out which segment
    return (
      <React.Fragment>
        <div className="wheel-wrapper">
          <div className="pad-wrapper">
            <div 
              id="pad" 
              style = {{transform:`rotate(${this.state.degree}deg)`}} >
              {
                this.state.showChallenge && 
                <span
                  className='challenge'
                  style = {{transform:`rotate(-${this.state.degree}deg)`}} > 
                  Title
                </span>
              }
            </div>
          </div>

        <div className="clipper">
          <div className="svg-spiral">
              <img src={spiral} alt="spiral turning" />
          </div>
        </div>

        <div className="svg-dashed">
          <img src={dashed} alt="dashed lines" />
        </div>

        <div className="svg-circle">
          <img src={circle} alt="circle" /> 
        </div>

        <button 
          id="play-btn"
          onClick = {this.play}
        >
        <span className="inner-btn"> Play </span> 
        </button>

        <div className="mobile-wrapper">
          <img className="mobile_dashed" src="./dashed_mobile.svg"  alt="dashed lines" />
          <img className="mobile_spiral" src="./spiral.svg" alt="spiral" />
        </div>
      </div>

      {/* <div className='brave-enough'>
        <h2>Are you<br/>brave enough<br/>to take the<br/>challenge?</h2>
      </div> */}

    </React.Fragment>
    )
  }
}


export default Game;
