import React from 'react';
import { Transition, CSSTransition } from 'react-transition-group';

import Logo from '../Logo';

import './TermsAndRules.css';

const TermsAndRules = props => {
  return (
    <CSSTransition 
      timeout={800} 
      in={true} 
      appear={true}
      mountOnEnter >
      {status => (
        <main className={`side-wrapper-animated flex bg-black pageTransition-${status}`}>
          <div className={`terms-rules textTransition-${status}`}>
            <h3>{props.title}</h3>
            <p>{props.description}</p>
          </div>
          <div className={`rt-logo logoTransition-${status}`}>
            <Logo color='light'/>
          </div>
        </main>
      )}
    </CSSTransition>
  );
};

export default TermsAndRules;
