import React, { Component } from "react";

import spiral from "./spiral.svg";
import dashed from "./dashed.svg";
import circle from "./circle.svg";
import handImg from "./hand1.png";

import "./Game.css";
import "./GameQueries.css";

class Game extends Component {
	state = {
		turns: 0,
		totalTurns: 0,
		degreeHand: 0,
		degreePad: 0,
		difference: 1000,
		last: new Date().getTime(),
		position: null,
		showChallenge: false,
		showPad: false,
		showHand: false
	};

	play = () => {
		let randomNum = Math.floor(Math.random() * 20 + 20);
		// let degreeHand = 90 * (Math.random() * 10 + 5);

		this.setState(
			{
				turns: randomNum * 90,
				totalTurns: randomNum,
				// degreeHand: degreeHand,
				showHand: true
			},
			() => {
				setTimeout(this.callRenderChallenge, 1000);
			}
		);
	};

	callRenderChallenge = () => {
		requestAnimationFrame(this.renderChallenge);
	};

	renderChallenge = () => {
		const { turns, totalTurns, degreeHand } = this.state;

		let position = ((totalTurns * 90) / 360) % 1;
		let finalPosition = (degreeHand / 360) % 1;

		let now = new Date().getTime();

		if (turns === 0) {
			console.log("finalposition: ", finalPosition);

			cancelAnimationFrame(this.renderChallenge);

			this.setState({
				position: position,
				totalTurns: 0,
				showChallenge: true,
				difference: 1000
			});
			return;
		}

		if (!this.state.last || now - this.state.last > this.state.difference) {
			let positionHand = ((degreeHand - 32 / 360) % 1).toFixed(2);
			// console.log(positionHand);

			this.setState(prevState => ({
				last: now,
				showPad: true,
				showChallenge: false,
				turns: prevState.turns - 1,
				degreeHand: prevState.degreeHand + 1,
				difference:
					prevState.turns >= prevState.turns / 5
						? prevState.difference * 0.85
						: prevState.difference * 1.25
			}));

			if (positionHand >= 0.0 && positionHand <= 0.24) {
				// console.log("top left");
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 0)
				}));
			}
			if (positionHand >= 0.25 && positionHand <= 0.49) {
				// console.log("top right");
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 90)
				}));
			}
			if (positionHand >= 0.5 && positionHand <= 0.74) {
				// console.log("bottom right");
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 180)
				}));
			}
			if (positionHand >= 0.75 && positionHand <= 0.99) {
				// console.log("bottom left");
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 270)
				}));
			}
		}
		requestAnimationFrame(this.renderChallenge);
	};

	render() {
		const { degreePad, degreeHand, position, showPad, showHand } = this.state;
		// console.log(
		// 	"Hand degree position live: ",
		// 	((degreeHand / 360) % 1).toFixed(2)
		// );

		let challengeTitle = null;

		if (this.state.showChallenge) {
			challengeTitle = (
				<p
					className="challenge"
					style={{ transform: `rotate(-${degreePad}deg)` }}
				>
					{position === 0 ? (
						<span id="pg">
							Parson's <br />
							Green
						</span>
					) : null}
					{position === 0.25 ? (
						<span id="bny">
							Brave
							<br />
							New
							<br />
							York
						</span>
					) : null}
					{position === 0.5 ? (
						<span id="st">
							Sex
							<br />
							Toys
						</span>
					) : null}
					{position === 0.75 ? (
						<span id="sb">
							Sheer
							<br />
							Bravery
						</span>
					) : null}
				</p>
			);
		}
		return (
			<React.Fragment>
				<div className="wheel-wrapper">
					<div className="pad-wrapper">
						{showPad && (
							<div id="pad" style={{ transform: `rotate(${degreePad}deg)` }}>
								{challengeTitle}
							</div>
						)}
					</div>

					<div className="clipper">
						<div className="svg-spiral spin">
							<img src={spiral} alt="spiral turning" />
						</div>
					</div>

					<div className="svg-dashed">
						<img src={dashed} alt="dashed lines" />
					</div>

					<div className="svg-circle">
						<img src={circle} alt="circle" />
					</div>

					<div className="outer-btn">
						<span
							className={
								showHand ? "fade-out-trans-scale inner-btn" : "inner-btn"
							}
						>
							Play
						</span>

						<div
							className={showHand ? "clipper-btn" : "clipper-btn"}
							style={{ transform: `rotate(${degreeHand - 135}deg)` }}
						>
							<button id="play-btn" onClick={this.play}>
								{showHand && (
									<img
										className="slide-in-hand hand-btn"
										src={handImg}
										alt="hand"
									/>
								)}
							</button>
						</div>
					</div>

					<div className="mobile-wrapper">
						<img
							className="mobile_dashed"
							src="./dashed_mobile.svg"
							alt="dashed lines"
						/>
						<img className="mobile_spiral" src="./spiral.svg" alt="spiral" />
					</div>
				</div>

				<div
					className={showHand ? "fade-out-scale brave-enough" : "brave-enough"}
				>
					<h2>
						Are you
						<br />
						brave enough
						<br />
						to take the
						<br />
						challenge?
					</h2>
				</div>
			</React.Fragment>
		);
	}
}

export default Game;
