import React, { Component } from "react";

import spiral from "./spiral.svg";
import dashed from "./dashed.svg";
import circle from "./circle.svg";
import handImg from "./hand1.png";

import "./Game.css";
import "./GameQueries.css";

class Game extends Component {
	state = {
		degreesToSpin: 0,
		totalDegrees: 0,
		degreeHand: 0,
		degreePad: 0,
		difference: 500,
		last: new Date().getTime(),
		speedVariation: null,
		position: null,
		showChallenge: false,
		showPad: false,
		showHand: false
	};
	play = () => {
		let randomNum = Math.floor(Math.random() * 5 + 2);

		this.setState(
			{
				degreesToSpin: randomNum * 90, // will be deducted
				totalDegrees: randomNum * 90, // will always be the same
				showHand: true
			},
			() => {
				setTimeout(this.callRenderChallenge, 500);
			}
		);
	};

	callRenderChallenge = () => {
		requestAnimationFrame(this.renderChallenge);
	};

	getSpeed = () => {
		let { degreesToSpin, totalDegrees, difference } = this.state;

		if (degreesToSpin <= totalDegrees / 2) {
			console.log("slow?");
			this.setState(prevState => ({
				speedVariation: (prevState.speedVariation = difference * 1.25)
			}));
		}

		if (degreesToSpin > totalDegrees / 2) {
			console.log("fast?");
			this.setState(prevState => ({
				speedVariation: (prevState.speedVariation = difference * 1.25)
			}));
		}

		return;
	};

	renderChallenge = () => {
		const {
			last,
			difference,
			degreesToSpin,
			totalDegrees,
			degreeHand,
			speedVariation
		} = this.state;

		let position = (totalDegrees / 360) % 1;

		let now = new Date().getTime();

		if (degreesToSpin === 0) {
			cancelAnimationFrame(this.renderChallenge);

			this.setState({
				position: position,
				totalDegrees: 0,
				showChallenge: true,
				difference: 500
			});
			return;
		}

		if (!last || now - last > difference) {
			let positionHand = (degreeHand / 360) % 1;
			console.log("Difference: ", this.state.difference);
			this.setState(prevState => ({
				last: now,
				showPad: true,
				showChallenge: false,
				degreesToSpin: prevState.degreesToSpin - 1,
				degreeHand: prevState.degreeHand + 1,
				difference: speedVariation
			}));
			this.getSpeed();
			// console.log("degrees to spin: ", this.state.degreesToSpin);

			// console.log("TURNS: ", this.state.degreesToSpin);
			// console.log("Total Turns/2 = ", this.state.totalDegrees / 2);
			// console.log("Difference: ", this.state.difference);  ---> result= 0 always
			// console.log("Last = now: ", this.state.last);
			// console.log("NOW - LAST= ", now - this.state.last);

			if (positionHand >= 0.9 || (positionHand >= 0 && positionHand <= 0.14)) {
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 0)
				}));
			}
			if (positionHand >= 0.15 && positionHand <= 0.39) {
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 90)
				}));
			}
			if (positionHand >= 0.4 && positionHand <= 0.64) {
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 180)
				}));
			}
			if (positionHand >= 0.65 && positionHand <= 0.89) {
				this.setState(prevState => ({
					degreePad: (prevState.degreePad = 270)
				}));
			}
		}
		requestAnimationFrame(this.renderChallenge);
	};

	render() {
		const { degreePad, degreeHand, position, showPad, showHand } = this.state;

		let challengeTitle = null;

		if (this.state.showChallenge) {
			challengeTitle = (
				<p
					className="challenge"
					style={{ transform: `rotate(-${degreePad}deg)` }}
				>
					{position === 0 ? (
						<span id="pg">
							Parson's <br />
							Green
						</span>
					) : null}
					{position === 0.25 ? (
						<span id="bny">
							Brave
							<br />
							New
							<br />
							York
						</span>
					) : null}
					{position === 0.5 ? (
						<span id="st">
							Sex
							<br />
							Toys
						</span>
					) : null}
					{position === 0.75 ? (
						<span id="sb">
							Sheer
							<br />
							Bravery
						</span>
					) : null}
				</p>
			);
		}
		return (
			<React.Fragment>
				<div className="wheel-wrapper">
					<div className="pad-wrapper">
						{showPad && (
							<div id="pad" style={{ transform: `rotate(${degreePad}deg)` }}>
								{challengeTitle}
							</div>
						)}
					</div>

					<div className="clipper">
						<div className="svg-spiral spin">
							<img src={spiral} alt="spiral turning" />
						</div>
					</div>

					<div className="svg-dashed">
						<img src={dashed} alt="dashed lines" />
					</div>

					<div className="svg-circle">
						<img src={circle} alt="circle" />
					</div>

					<div className="outer-btn">
						<span
							className={
								showHand ? "fade-out-trans-scale inner-btn" : "inner-btn"
							}
						>
							Play
						</span>

						<div
							className={showHand ? "clipper-btn" : "clipper-btn"}
							style={{ transform: `rotate(${degreeHand - 135}deg)` }}
						>
							<button id="play-btn" onClick={this.play}>
								{showHand && (
									<img
										className="slide-in-hand hand-btn"
										src={handImg}
										alt="hand"
									/>
								)}
							</button>
						</div>
					</div>

					<div className="mobile-wrapper">
						<img
							className="mobile_dashed"
							src="./dashed_mobile.svg"
							alt="dashed lines"
						/>
						<img className="mobile_spiral" src="./spiral.svg" alt="spiral" />
					</div>
				</div>

				<div
					className={showHand ? "fade-out-scale brave-enough" : "brave-enough"}
				>
					<h2>
						Are you
						<br />
						brave enough
						<br />
						to take the
						<br />
						challenge?
					</h2>
				</div>
			</React.Fragment>
		);
	}
}

export default Game;
