import React, {Component} from 'react';

import spiral from './spiral.svg';
import dashed from './dashed.svg';
import circle from './circle.svg';

import './Game.css';
import './GameQueries.css';


class Game extends Component {
  state = {
    totalTurns : 0,
    degree: 0,
  }


  play = (e) => {
    e.preventDefault();
    this.getRandomNum();

    requestAnimationFrame(this.handlePlay);
  }

  getRandomNum = () => {
    this.setState({
      totalTurns : Math.floor((Math.random() * 10) + 5)
    },
     () => { this.handlePlay() }
    );
  }

  handlePlay = () => {
    console.log('plaaay', this.state.totalTurns)
    if(this.state.totalTurns === 0) {
      return;
    }

    if(this.state.totalTurns > 0) {
      this.setState((prevState) => ({
        totalTurns: prevState.totalTurns - 1,
        degree: prevState.degree + 90
      }))
    }
    requestAnimationFrame(this.handlePlay);
  }

  render() {
    console.log('TURNS: ', this.state.totalTurns);

    return (
      <React.Fragment>
        <div className="wheel-wrapper">
          <div className="pad-wrapper">
            <div className="pad active" style = {{transform:`rotate(${this.state.degree}deg)`}}>
              <span className='challenge'>
                Title
              </span>
            </div>
          </div>

        <div className="clipper">
          <div className="svg-spiral">
              <img src={spiral} alt="spiral turning" />
          </div>
        </div>

        <div className="svg-dashed">
          <img src={dashed} alt="dashed lines" />
        </div>

        <div className="svg-circle">
          <img src={circle} alt="circle" /> 
        </div>

        <button 
          id="play-btn"
          onClick = {this.play}
          >Play 
        </button>

        <div className="mobile-wrapper">
          <img className="mobile_dashed" src="./dashed_mobile.svg"  alt="dashed lines" />
          <img className="mobile_spiral" src="./spiral.svg" alt="spiral" />
        </div>
      </div>

      {/* <div className='brave-enough'>
        <h2>Are you<br/>brave enough<br/>to take the<br/>challenge?</h2>
      </div> */}

    </React.Fragment>
    )
  }
}


export default Game;
