import React, {Component} from 'react';

import spiral from './spiral.svg';
import dashed from './dashed.svg';
import circle from './circle.svg';

import './Game.css';
import './GameQueries.css';


class Game extends Component {
  state = {
    totalTurns : 0,
    degree: 0,
    difference: 1000,
    last: new Date().getTime()
  }

  componentDidMount = () => {
    const pad = document.getElementById("pad");
    console.log('componentDidMount',pad.getBoundingClientRect());
  }

  componentDidUpdate = () =>{
    const pad = document.getElementById("pad");

    console.log('Update:',pad.getBoundingClientRect());
  }

  play = (e) => {
    e.preventDefault();
    this.getRandomNum();

    requestAnimationFrame(this.handlePlay);
  }

  getRandomNum = () => {
    this.setState({
      totalTurns : Math.floor((Math.random() * 20) + 10)
    },
     () => { this.handlePlay() }
    );
  }

  handlePlay = () => {
    let now = new Date().getTime();

    if(this.state.totalTurns === 0) {
      this.setState({
        difference: 1000
      });
      return;
    }

    if(!this.state.last || now - this.state.last > this.state.difference) {

      this.setState((prevState) => ({
        last: now,
        totalTurns: prevState.totalTurns - 1,
        degree: prevState.degree + 90,
        difference: (prevState.totalTurns >= prevState.totalTurns / 5) ? prevState.difference * 0.85 : prevState.difference * 1.25
      }))
    }
    requestAnimationFrame(this.handlePlay);
  }

  render() {

    return (
      <React.Fragment>
        <div className="wheel-wrapper">
          <div className="pad-wrapper">
            <div 
              id="pad" 
              style = {{transform:`rotate(${this.state.degree}deg)`}} >
              <span 
                className='challenge'>
                Title
              </span>
            </div>
          </div>

        <div className="clipper">
          <div className="svg-spiral">
              <img src={spiral} alt="spiral turning" />
          </div>
        </div>

        <div className="svg-dashed">
          <img src={dashed} alt="dashed lines" />
        </div>

        <div className="svg-circle">
          <img src={circle} alt="circle" /> 
        </div>

        <button 
          id="play-btn"
          onClick = {this.play}
        >
        <span className="inner-btn"> Play </span> 
        </button>

        <div className="mobile-wrapper">
          <img className="mobile_dashed" src="./dashed_mobile.svg"  alt="dashed lines" />
          <img className="mobile_spiral" src="./spiral.svg" alt="spiral" />
        </div>
      </div>

      {/* <div className='brave-enough'>
        <h2>Are you<br/>brave enough<br/>to take the<br/>challenge?</h2>
      </div> */}

    </React.Fragment>
    )
  }
}


export default Game;
