import React, {Component} from 'react';

import spiral from './spiral.svg';
import dashed from './dashed.svg';
import circle from './circle.svg';
import hand from './hand.png';

import './Game.css';
import './GameQueries.css';


class Game extends Component {
  state = {
    turns : 0,
    totalTurns: 0,
    degree: 0,
    difference: 1000,
    last: new Date().getTime(),
    position: null,
    showChallenge: false,
    showPad: false
  }

  play = (e) => {
    let randomNum = Math.floor((Math.random() * 20) + 10);

    e.preventDefault();
    this.setState({
      turns : randomNum,
      totalTurns: randomNum,
      showPad: true
    },
     () => { requestAnimationFrame(this.renderChallenge) }
    );
  }

  renderChallenge = () => {
    const { turns, totalTurns } = this.state;
    let position = (totalTurns * 90) / 360 % 1;
    let now = new Date().getTime();

    if(turns === 0) {
      this.setState({
        position: position,
        totalTurns: 0,
        showChallenge: true,
        difference: 1000
      });
      return;
    }

    if(!this.state.last || now - this.state.last > this.state.difference) {

      this.setState((prevState) => ({
        last: now,
        position: null, /* does not work */
        showChallenge: false,
        turns: prevState.turns - 1,
        degree: prevState.degree + 90,
        difference: (prevState.turns >= prevState.turns / 5) ? prevState.difference * 0.85 : prevState.difference * 1.25
      }))
    }
    requestAnimationFrame(this.renderChallenge);
  }

  render() {
    const { degree, position, showPad } = this.state;
    let challengeTitle = null;

    if(this.state.showChallenge) {
      challengeTitle = (
        <p
          className='challenge'
          style = {{transform:`rotate(-${degree}deg)`}} 
        > 
          {position === 0 ? <span id='pg'>Parson's <br/>Green</span> : null}
          {position === 0.25 ? <span id='bny'>Brave<br/>New<br/>York</span> : null}
          {position === 0.5 ? <span id='st'>Sex<br/>Toys</span> : null}
          {position === 0.75 ? <span id='sb'>Sheer<br/>Bravery</span> : null}
        </p>
      )
    }

    return (
      <React.Fragment>
        <div className="wheel-wrapper">
          <div className="pad-wrapper">
            { 
              showPad &&
                <div 
                  id="pad" 
                  style = {{transform:`rotate(${degree}deg)`}} >
                  { challengeTitle }
                </div>
            }
          </div>

        <div className="clipper">
          <div className="svg-spiral">
              <img src={spiral} alt="spiral turning" />
          </div>
        </div>

        <div className="svg-dashed">
          <img src={dashed} alt="dashed lines" />
        </div>

        <div className="svg-circle">
          <img src={circle} alt="circle" /> 
        </div>

        <button 
          id="play-btn"
          onClick = {this.play}
        >
          <span 
            className = { showPad ? "fade-out inner-btn" : "inner-btn"}> 
            Play </span>
          <img 
            className = { showPad ? "fade-in hand-btn" : "hand-btn"}
            src={hand} 
            alt='hand'/> 
        </button>

        <div className="mobile-wrapper">
          <img className="mobile_dashed" src="./dashed_mobile.svg"  alt="dashed lines" />
          <img className="mobile_spiral" src="./spiral.svg" alt="spiral" />
        </div>
      </div>

      <div 
        className={showPad ? 'fade-out brave-enough' : 'brave-enough' }>
        <h2>Are you<br/>brave enough<br/>to take the<br/>challenge?</h2>
      </div>

    </React.Fragment>
    )
  }
}


export default Game;
