import React, { Component } from "react";

import spiral from "./spiral.svg";
import dashed from "./dashed.svg";
import circle from "./circle.svg";
import handImg from "./hand.png";

import "./Game.css";
import "./GameQueries.css";

class Game extends Component {
	state = {
		turns: 0,
		totalTurns: 0,
		degree: 0,
		difference: 1000,
		last: new Date().getTime(),
		position: null,
		showChallenge: false,
		showPad: false,
		showHand: false
	};

	play = e => {
		let randomNum = Math.floor(Math.random() * 20 + 20);

		e.preventDefault();

		this.setState(
			{
				turns: randomNum,
				totalTurns: randomNum,
				showHand: true
			},
			() => {
				setTimeout(this.callRenderChallenge, 1000);
			}
		);
	};

	callRenderChallenge = () => {
		// this.setState({
		//   showPad: true
		// });

		requestAnimationFrame(this.renderChallenge);
	};

	renderChallenge = () => {
		const { turns, totalTurns } = this.state;
		let position = ((totalTurns * 90) / 360) % 1;
		let now = new Date().getTime();

		if (turns === 0) {
			cancelAnimationFrame(this.renderChallenge);

			this.setState({
				position: position,
				totalTurns: 0,
				showChallenge: true,
				difference: 1000
			});
			return;
		}

		if (!this.state.last || now - this.state.last > this.state.difference) {
			this.setState(prevState => ({
				last: now,
				position: null /* does not work - TO FIX */,
				showPad: true,
				showChallenge: false,
				turns: prevState.turns - 1,
				degree: prevState.degree + 90,
				difference:
					prevState.turns >= prevState.turns / 5
						? prevState.difference * 0.85
						: prevState.difference * 1.25
			}));
		}
		requestAnimationFrame(this.renderChallenge);
	};

	render() {
		const { degree, position, showPad, showHand } = this.state;

		let challengeTitle = null;

		if (this.state.showChallenge) {
			challengeTitle = (
				<p className="challenge" style={{ transform: `rotate(-${degree}deg)` }}>
					{position === 0 ? (
						<span id="pg">
							Parson's <br />
							Green
						</span>
					) : null}
					{position === 0.25 ? (
						<span id="bny">
							Brave
							<br />
							New
							<br />
							York
						</span>
					) : null}
					{position === 0.5 ? (
						<span id="st">
							Sex
							<br />
							Toys
						</span>
					) : null}
					{position === 0.75 ? (
						<span id="sb">
							Sheer
							<br />
							Bravery
						</span>
					) : null}
				</p>
			);
		}
		return (
			<React.Fragment>
				<div className="wheel-wrapper">
					<div className="pad-wrapper">
						{showPad && (
							<div id="pad" style={{ transform: `rotate(${degree}deg)` }}>
								{challengeTitle}
							</div>
						)}
					</div>

					<div className="clipper">
						<div className="svg-spiral spin">
							<img src={spiral} alt="spiral turning" />
						</div>
					</div>

					<div className="svg-dashed">
						<img src={dashed} alt="dashed lines" />
					</div>

					<div className="svg-circle">
						<img src={circle} alt="circle" />
					</div>

					<div className="outer-btn">
						<span
							className={
								showHand ? "fade-out-trans-scale inner-btn" : "inner-btn"
							}
						>
							Play
						</span>

						<div
							className={showHand ? "clipper-btn" : "clipper-btn"}
							style={{ transform: `rotate(${degree - 135}deg)` }}
						>
							<button id="play-btn" onClick={this.play}>
								{showHand && (
									<img
										className="slide-in-hand hand-btn"
										src={handImg}
										alt="hand"
									/>
								)}
							</button>
						</div>
					</div>

					<div className="mobile-wrapper">
						<img
							className="mobile_dashed"
							src="./dashed_mobile.svg"
							alt="dashed lines"
						/>
						<img className="mobile_spiral" src="./spiral.svg" alt="spiral" />
					</div>
				</div>

				<div
					className={showHand ? "fade-out-scale brave-enough" : "brave-enough"}
				>
					<h2>
						Are you
						<br />
						brave enough
						<br />
						to take the
						<br />
						challenge?
					</h2>
				</div>
			</React.Fragment>
		);
	}
}

export default Game;
