import React from "react";

import { LIGHT, DARK } from "../../colors.js";

const Hamburger = props => {
	const colors = {
		light: LIGHT,
		dark: DARK
	};

	const styles = {
		lineTop: {
			transform: props.isMobileMenuOpen ? "rotate(45deg)" : "none",
			transformOrigin: "top left",
			marginBottom: "5px"
		},
		lineMiddle: {
			opacity: props.isMobileMenuOpen ? 0 : 1,
			transform: props.isMobileMenuOpen ? "translateX(-16px)" : "none"
		},
		lineBottom: {
			transform: props.isMobileMenuOpen
				? "translateX(-1px) rotate(-45deg)"
				: "none",
			transformOrigin: "top left",
			marginTop: "5px"
		}
	};

	return (
		<button className="hamburger flex f-col" onClick={props.click}>
			<span className="line line1" style={{ ...styles.line1 }} />
			<span className="line line2" style={{ ...styles.line2 }} />
			<span className="line line3" style={{ ...styles.line3 }} />
		</button>
	);
};

export default Hamburger;
